import React, { useReducer, useState } from 'react'
import './SignUp.css'
const initialState={
    nameValue:{
        name:'',
        isValid:false,
    },
    emailValue:{
  email:'',
  isValid:false,
    },
    passwordValue:{
        password:'',
        isValid:false,
    }
}
const UserReducer = (state, action) => {
	switch (action.type) {
		case 'NAME':
			return {
				...state,
				nameValue: {
					name: action.value,
					isValid: nameValid.test(action.value),
				},
			}
		case 'EMAIL':
			return {
				...state,
				emailValue: {
					email: action.value,
					isValid: action.test(action.value),
				},
			}
            case'PASSWORD':
            return{
                ...state,
                passwordValue:{
                    password:action.value,
                    isValid:action.value.length > 5,
                }
            }

		default:
			break
	}
}
function SignUp(props) {
	const [user, dicpatchUser] = useReducer(UserReducer, initialState)
	const NameChangeHandler = (event) => {
	dicpatchUser({type:'NAME', value:event.target.value})
	}
	const EmailChangeHandler = (event) => {
		dicpatchUser({type:'EMAIL',value:event.target.value})
	}
	const PasswordChangeHandler = (event) => {
		dicpatchUser({type:'PASSWORD',value:event.target.value})
	}
	const SubmitHandler = (event) => {
		event.preventDefault()
		// const newData = {
		// 	name: name,
		// 	email: email,
		// 	password: password,
		// 	id: Math.random().toString(),
		// }
		// console.log(newData)
	}

	// const nameValid = RegExp(/[0-9]/)
	// if (!nameValid.test(String(name).toLocaleLowerCase)) {
	// 	console.log('error')
	// }

	return (
		<form onSubmit={SubmitHandler} className='input_form'>
			<label>username</label>
			<input
				onChange={NameChangeHandler}
				type='text'
				placeholder='username'
			/>

			<label>login</label>
			<input
				onChange={EmailChangeHandler}
				type='email'
				placeholder='login'
			/>

			<label>password</label>
			<input
				onChange={PasswordChangeHandler}
				type='password'
				placeholder='password'
			/>

			<button>OK</button>
		</form>
	)
}

export default SignUp
